        setTimeout(function () {
            $(".pip-value").text("0.1");
        }, 3000);
    }
    let counter = 0;
    var t = setInterval(function() {
        counter++;
        if(counter > 4) counter = 1;
        /* each second the function drawSec is called */
        animateTheLP(counter);
    }, 3000);

