<script>
    $(window).on("scroll", function() {
        if($(window).scrollTop() > 50) {
            $(".tmgm-header").addClass("on-scroll");
            if($(window).width() >= 1440) {
                $(".tmgm-main-menu").css("display", "none");
                $(".tmgm-main-menu-on-scroll").css("display", "flex");
                $(".tmgm-header-container").css("max-width", "1500px");
                $(".hide-on-scroll").css("display", "none")
            }
        } else {
            //remove the background property so it comes transparent again (defined in your css)
            $(".tmgm-header").removeClass("on-scroll");
            if($(window).width() >= 1440) {
                $(".tmgm-main-menu").css("display", "");
                $(".tmgm-header").removeClass("on-scroll");
                $(".tmgm-main-menu-on-scroll").css("display", "none");
                $(".tmgm-header-container").css("max-width", "1224px");
                $(".hide-on-scroll").css("display", "flex")
            }
        }
    });
    $(window).resize(function() {
        var width = $(window).width();
        if($(".tmgm-header").hasClass("on-scroll") && width < 1024) {
            $(".tmgm-main-menu-on-scroll").css("display", "none");
            $(".tmgm-header-container").css("max-width", "1224px");
        } else if($(".tmgm-header").hasClass("on-scroll") && width > 1024) {
            $(".tmgm-main-menu-on-scroll").css("display", "flex");
