<script>
    function animateTheLP (counter) {
        const sceneClass = $(".LP-chart").attr("class").split(" ")[1]
        $(".LP-chart").removeClass(sceneClass).addClass(`scene-${counter}`)
        $(".pl-card").find(`:nth-child(${counter})`).css("display", "block");
        $(".pl-card").find(`:not(:nth-child(${counter}))`).css("display", "none");

        setTimeout(function () {
            $(".pip-value").text("0.0");
        }, 1500);

        setTimeout(function () {
            $(".pip-value").text("0.1");
        }, 3000);
    }
    let counter = 0;
    var t = setInterval(function() {
        counter++;
        if(counter > 4) counter = 1;
        /* each second the function drawSec is called */
        animateTheLP(counter);
    }, 3000);

</script>
