            $("form[tag=member-registration-form]").find(".alert-danger").addClass("show");
            $("form[tag=member-registration-form]").find(".alert-danger").find("span").html(errorMessage)
        }
        var validateForm =  function () {
            const regName = /^[a-zA-Z\s]+$/;
            const regTel = /^[0-9]{7,32}$/;
            const regEmail = /^([A-Za-z0-9]+[_|\-|.]a?)*[a-zA-Z0-9]+@([A-Za-z0-9]+[.|-]?)*[A-Za-z0-9]+\.[A-Za-z]{2,}$/;
            const regPassword = /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{8,15}$/;
            let validated = true;
            $(".input-error").text("");
            $("form[tag=member-registration-form] input").each(function(index, el) {
                const _name = $(el).attr("name");
                const _val = $(el).val();
                // Validate first name.
                if (_name === "firstName") {
                    if (!_val) {
                        $("#firstNameError").text("The First Name field is required.");
                        checkKey = false;
                        validated = false;
                    } else if (_val && !regName.test(_val)) {
                        $("#firstNameError").text("The First Name format is invalid.");
                        checkKey = false;
                        validated = false;
                    }
                }
                // Validate middle name.
                // if (_name === "middleName") {
                //     if (!_val) {
                //         $("#middleNameError").text("Please enter valid middle name.");
                //         checkKey = false;
                //         validated = false;
                //     }
                // }
                // Validate last name.
                if (_name === "lastName") {
                    if (!_val) {
                        $("#lastNameError").text("The Last Name field is required.");
                        checkKey = false;
                        validated = false;
                    } else if (_val && !regName.test(_val)) {
                        $("#lastNameError").text("The Last Name format is invalid.");
                        checkKey = false;
                        validated = false;
                    }
                }
                // Validate mobile number.
                if (_name === "phone-number") {
                    if (!_val) {
                        $("#phoneNumberError").text("The Phone Number field is required.");
                        checkKey = false;
                        validated = false;
                    }  else if (_val && !regTel.test(_val)) {
                        $("#phoneNumberError").text("The Phone Number format is invalid.");
                        checkKey = false;
                        validated = false;
                    }
                }
                // Validate email.
                if (_name === "email") {
                    if ((_val && !regEmail.test(_val)) || !_val) {
                        $("#emailError").text("The Email field is required.");
                        checkKey = false;
                        validated = false;
                    }  else if (_val && !regEmail.test(_val)) {
                        $("#emailError").text("The Email format is invalid.");
                        checkKey = false;
                        validated = false;
                    }
                }
            })

            return validated;
        }

        var submitForm = function () {
            params = {
                first_name: $("input[name="firstName"]").val(),
                middle_name: $("input[name="middleName"]").val(),
                last_name: $("input[name="lastName"]").val(),
                country: $("#select-state").val(),
                mobile: $("input[name="phone-number"]").val(),
                email: $("input[name="email"]").val().toLowerCase(),
                country_code: $("#country-dial-code").val(),
                send_email: 0,
                register_account_type: formType === "live" ? 1 : 2,
                language: "en",
                referral_code: null,
                redirect: formType === "live" ? "apply-live-ta" : "apply-demo-ta"
            }
            const requestUrl = "/api/register"
            $.post(requestUrl, params, function (res) {
                const data = JSON.parse(res);
                if (data) {
                    if (!data.message) {
                        const _code = data.redirect_address.split("sign=");
                        const _url = encodeURIComponent(_code[1]);
                        if (data.redirect_address && $(window).width() >= 1024) window.location.href = _code[0] + "sign=" + _url + "&language=en";
                        if (data.redirect_address && $(window).width() < 1024) window.location.href = "<?php echo($domain_url)?>/official-redirect?sign=" + _url + "&language=en";
                    } else {
                        grecaptcha.reset(recaptchaWidgets["google_recaptcha_1"]);
                        error(registerErrorMessages[data.code]);
                    }
                }
            }).fail(function (response) {
                grecaptcha.reset(recaptchaWidgets["google_recaptcha_1"]);
                error(response.responseJSON.message);
            });
        }
        $("#register-submit-btn").click(function() {
            var validated = validateForm();
            if(validated) {
                $("#register-submit-btn").attr("disabled", true);
                $("#register-submit-btn").removeClass("primary-button").addClass("primary-button-disabled");
                submitForm();
                $("#register-submit-btn").attr("disabled", false);
                $("#register-submit-btn").removeClass("primary-button-disabled").addClass("primary-button");
            } else {
                grecaptcha.reset(recaptchaWidgets["google_recaptcha_1"]);
            }
        })
        $("form[tag=member-registration-form] input").keyup(function() {
            if($("form[tag=member-registration-form]").find(".alert-danger").hasClass("show")) {
                $("form[tag=member-registration-form]").find(".alert-danger").removeClass("show");
            }
        })
        $("#select-state").on("select2:select", function (e) {
            // auto select phone code on select country
            $("#country-dial-code").val(e.params.data.phoneCo).trigger("change");
        });
        $( "#registerModal" ).on("shown.bs.modal", function(){
            loadNumber();
        });
    })
