        menuIndex = $(this).index();

        if(!$(this).parent().parent().parent().find(".tmgm-submenu-area").children().eq(menuIndex).hasClass("active")) {
            $(this).parent().parent().parent().find(".tmgm-submenu-area").children().eq(menuIndex).addClass("active");
        }

        $(".has-submenu").removeClass("active");
        // $(".has-submenu").find(".tmgm-submenu").removeClass("active")
        if(!$(this).hasClass("active")) $(this).addClass("active");
        else $(this).removeClass("active");
        if(!$(this).find(".tmgm-submenu").hasClass("active")) $(this).find(".tmgm-submenu").addClass("active");
        else $(this).find(".tmgm-submenu").removeClass("active");
    })
